// ocr-frontend/src/components/SignUp.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      // Send OTP to the user's email
      await axios.post('http://localhost:5000/api/sendOTP', { email });

      // Prompt user to enter OTP
      const enteredOTP = prompt('Enter OTP sent to your email');

      // Verify OTP and complete signup
      const response = await axios.post('http://localhost:5000/api/signup', { email, password, otp: enteredOTP });
      localStorage.setItem('token', response.data.token);
      navigate('/ocr');
    } catch (error) {
      console.error(error.message);
      alert('Sign Up failed');
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSignUp}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Sign Up</button>
      </form>
      <p>Already have an account? <Link to="/login">Login</Link></p>
    </div>
  );
};

export default SignUp;
