// src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router } from 'react-router-dom';
import { Routes, Route, Navigate } from 'react-router-dom';

import Login from './components/Login';
import Signup from './components/Signup';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';
import PrivateRoute from './components/PrivateRoute';
import './App.css';

function OCRApp() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [ocrResult, setOcrResult] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleOcr = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post('http://localhost:5000/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setOcrResult(response.data.text);
      } catch (error) {
        console.error(error.message);
      }
    }
  };

  return (
    <div className="App">
      <h1>OCR React App</h1>
      <p className="description">
        This application allows you to upload an image and extract the text from it using OCR (Optical Character Recognition). 
        Simply choose an image file and click on "Get Text" to see the magic!
      </p>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleOcr}>Get Text</button>
      <div className="result">
        <h2>Result:</h2>
        <p>{ocrResult}</p>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="*" element={<Navigate to="/login" />} />
        <Route path="/forgot-password" component={ForgotPassword} />
        <Route path="/reset-password" component={ResetPassword} />
        <PrivateRoute exact path="/" component={OCRApp} />

      </Routes>
    </Router>
  );
}

export default App;
