// src/components/OCR.js
import React, { useState } from 'react';
import axios from 'axios';

const OCR = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [ocrResult, setOcrResult] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleOcr = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        // Replace with your backend API endpoint for OCR
        const response = await axios.post('http://localhost:5000/ocr', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setOcrResult(response.data.text);
      } catch (error) {
        console.error('OCR error:', error);
        alert('Error during OCR. Please try again later.');
      }
    }
  };

  return (
    <div>
      <h2>OCR Page</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleOcr}>Get Text</button>
      <div className="result">
        <h3>Result:</h3>
        <p>{ocrResult}</p>
      </div>
    </div>
  );
};

export default OCR;
