// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'; // Change import
import Login from './components/Login';
import Signup from './components/Signup';
import OCR from './components/OCR';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');

  return (
    <Router>
      <div className="App">
        <Route exact path="/">
          {isLoggedIn ? <Redirect to="/ocr" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
        </Route>
        <Route path="/signup">
          {isLoggedIn ? <Redirect to="/ocr" /> : <Signup setIsLoggedIn={setIsLoggedIn} />}
        </Route>
        <Route path="/ocr">
          {!isLoggedIn ? <Redirect to="/" /> : <OCR />}
        </Route>
      </div>
    </Router>
  );
}

export default App;
